    render() {
        return (
            <div className="container">
                <div className="cake">

                </div>
                <div className="option">
                    <form>
                        <h1>{"Coberturas"}</h1>

                        <select>
                            <option value="Fondant" ref={this.cobertura}>Fondant</option>
                            <option value="Azul">Azul</option>
                            <option value="Verde" >Verde</option>
                            <option value="Rojo">Rojo</option>
                        </select><br />
                        <input type="radio" name="cob" value="2" option value="Azul" ref={this.cobertura}/>Crema Pastelera<br />
                        <input type="radio" name="cob" value="3" ref={this.cobertura}/>Crema de Chantilly <br />
                        <input type="radio" name="cob" value="4" ref={this.cobertura}/>Crema de Mantequilla<br />
                        <input type="radio" name="cob" value="5" ref={this.cobertura}/>Sin Cobertura<br />

                        <button>
                    Siguiente
                </button>
                    </form>
                </div>
                
            </div>
        )
    }
}


export default Coberturas;
