import React, {useState} from 'react';
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import {Dropdown,DropdownItem, DropdownMenu, DropdownToggle} from 'reactstrap';
import "./style.scss";
import ReactDOM from "react-dom";
import * as THREE from "three";


class InputSelect extends React.Component{
    constructor(props){
      super(props)
      this.actualizarState = this.actualizarState.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.state={activo:""};
    }
    actualizarState(e){
      const {name, value} = e.target;
      this.setState({value});
      this.props.actualizarState({
        name,value,error: value == "" ? true:false
      })
    }
    handleSubmit(e) {
      alert('A name was submitted: ' + this.state.value);
      e.preventDefault();
    }
    
    render(){
      return(
        <div>
  
  
        
        <div className="componente-input"  >
          <label htmlFor={"id-"+this.props.name}>{this.props.label}</label>
          <select
            id={"id-"+this.props.name}
            name={this.props.name}
            onChange={this.actualizarState}
            >
              {
                this.props.opciones.map((opcion,index)=>(
                <option hey={index} value={opcion.value}>{opcion.texto}</option>
                ))
              }            
          </select>
          
          
        </div>
        
        </div>
      )
    }
  }
export  default class Pastel extends React.Component{
    
    constructor(props){
        super(props);
        this.submit = this.submit.bind(this);
        this.actualizarState = this.actualizarState.bind(this);
        this.state = {
          
          opciones:{
            value:"",
            error:true
          }
        }
      }
      
      actualizarState(input){
        this.setState({
          ...this.state,
          [input.name]:{
            value:input.value,
            error:input.error
          }
        }, ()=>{console.log(this.state);});
        
      }
    
      submit(e){
        e.preventDefault();
        console.log(this.state)
        }
    
      render(){
        const valor =this.state.value;
            var scene = new THREE.Scene();
            var camera = new THREE.PerspectiveCamera( 75, window.innerWidth/window.innerHeight, 0.1, 1000 );
            var renderer = new THREE.WebGLRenderer();
            renderer.setSize( window.innerWidth/2, window.innerHeight/2 );
            document.body.appendChild( renderer.domElement );
            //var geometry = new THREE.CylinderGeometry( 2, 2, 1, 32 );
            var geometry;
    
            if (valor=='1') {
                
                geometry = new THREE.SphereGeometry(1, 16, 8);
              } else {
                
                geometry = new THREE.BoxGeometry(2, 2, 2);
              }
            var material = new THREE.MeshPhongMaterial({  color: 0xffe9fc,
                emissive: 0xc9669c,
                emissiveIntensity: 0.3,
                shininess: 0 });
            var cube = new THREE.Mesh( geometry, material );
            scene.add( cube );
    
            var geometry1 = new THREE.BoxGeometry( 1, 1, 1 );
            var material1 = new THREE.MeshPhongMaterial({  color: ( 'black' ),
                });
    
                
            var cube1 = new THREE.Mesh( geometry1, material1 );
            scene.add( cube1 ); 
            cube1.position.set(0,0.05,2);
            cube1.rotation.y = Math.PI / 4;
    
            
                   
            
            var light = new THREE.PointLight(0xFFFFFF);         
    
    light.position.set(-10, 15, 50);
    scene.add(light);
    camera.position.set(0,1.5,5);
            renderer.render( scene, camera ); 
        
       
        return(
          
          <div style={{textAlign: "center"}}>
            <h1 >{"Coberturas"}</h1>
            <div className={"d"+valor} >
                  
          
                  </div>
          <form onSubmit={this.submit} >
            
          
            <InputSelect
              name="opciones"
              label="Elige una opci√≥n:"
              actualizarState={this.actualizarState}
              opciones={[
                {value:"", texto:"Seleccione un opci√≥n..."},        
                {value:"1", texto:"Fondant"},
                {value:"2", texto:"Crema Pastelera"},
                {value:"3", texto:"Crema de Chantilly"},
                {value:"4", texto:"Crema de Mantequilla"},
                {value:"5", texto:"Sin Cobertura"}
                
                ]} />
    
    
            <button 
              disabled={ this.state.opciones.error}
              type="submit"
              className={
                this.state.opciones.error
                ? 
                "button-disable":"button"}>
              Siguiente
            </button>
           
          </form>
          
          
          </div>
        )
      }
    }
