import "./style.scss";
import React, { useState, useRef } from "react";

class Coberturas extends React.Component {
    
    constructor(props){
        super(props);
        this.cobertura=React.createRef();
    }

   
    render() {
        return (
            <div className="container">
                <div className="cake">

                </div>
                <div className="option">
                    <form onSubmit={this.submit}>
                        <h1>{"Coberturas"}</h1>

                        <select>
                            <option value="Fondant" >Fondant</option>
                            <option value="Azul">Azul</option>
                            <option value="Verde" >Verde</option>
                            <option value="Rojo">Rojo</option>
                        </select><br />
                        <input type="radio" name="cob" value="2" option value="Azul" />Crema Pastelera<br />
                        <input type="radio" name="cob" value="3" />Crema de Chantilly <br />
                        <input type="radio" name="cob" value="4" />Crema de Mantequilla<br />
                        <input type="radio" name="cob" value="5" />Sin Cobertura<br />

                        <button>
                    Siguiente
                </button>
                    </form>
                </div>
                
            </div>
        )
    }
}


export default Coberturas;
