import React, {useState,useRef} from "react";

class Coberturas extends React.Component{
    render(){
        return(
            <form>
                <h1>{"Coberturas"}</h1>
                
<input type="radio" name="transporte" value="1"/>Fondant
<select>
  <option value="Ford">Ford</option>
  <option value="Volvo" selected>Volvo</option>
  <option value="Fiat">Fiat</option>
</select>
<input type="radio" name="transporte" value="2"/>Crema Pastelera<br />
<input type="radio" name="transporte" value="3"/>Crema de Chantilly <br />
<input type="radio" name="transporte" value="4"/>Crema de Mantequilla<br />
<input type="radio" name="transporte" value="5"/>Sin Cobertura<br />


            </form>
        )
    }
}


export default Coberturas;
