import React, {useState} from 'react';
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import {Dropdown,DropdownItem, DropdownMenu, DropdownToggle} from 'reactstrap';
import "./style.scss";
import ReactDOM from "react-dom";
import * as THREE from "three";


export default class Pedido extends React.Component {
    constructor(props) {
        
        super(props);
       
        this.state = {
                Masa: 'Sin masa',
                Relleno: 'Sin relleno',
                Cobertura: 'Sin cubierta',
                Color: '#FFFFFF',
                Porciones: '1',
                Tematica:''

        };
    }
    seleccionM =(event)=> {this.setState({Masa:event.target.id})}
    seleccionR =(event)=> {this.setState({Relleno:event.target.id})}
    seleccionC =(event)=> {this.setState({Cobertura:event.target.id})}
    seleccionP =(event)=> {this.setState({Porciones:event.target.id})}
    seleccionT =(event)=> {this.setState({Tematica:event.target.id})}

    
    componentDidMount() {
        fetch('http://localhost:8000/pasteles/', {
            method: 'PUT',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(this.state)
        })
     }
     
     
    render() {
        
        
        return (
            <div className =" d-flex">
                <div className= "drawCake col-sm-4">
                    <Pastel></Pastel>
                </div>
                <div className ="opciones col-sm-4">
                <div style ={{margin:5+'px'}}>
                    <div class="dropdown">
                        <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-toggle="dropdown" aria-expanded="false">
                            Masa
                        </button>
                        <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                            <li><a class="dropdown-item"   onClick= {this.seleccionM} id="RedVelvet">RedVelvet</a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionM} id="Tres Leches"> Tres Leches</a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionM} id="Vainilla"> Vainilla </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionM} id="Chocolate">  Chocolate</a></li>
                        </ul>
                    </div>
                    
                </div>

                <div style ={{margin:5+'px'}}>
                    <div class="dropdown">
                        <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-toggle="dropdown" aria-expanded="false">
                            Relleno
                        </button>
                        <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                            <li><a class="dropdown-item"   onClick= {this.seleccionR} id="Arequipe">Arequipe</a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionR} id="Nutella"> Nutella</a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionR} id="Mermelada "> Mermelada  </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionR} id="Mermelada "> Mermelada  </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionR} id="Frutas  "> Frutas </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionR}  id="Chantilly "> Chantilly  </a></li>
                        </ul>
                    </div>
                    
                </div>

                <div style ={{margin:5+'px'}}>
                    <div class="dropdown">
                        <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-toggle="dropdown" aria-expanded="false">
                             Cubierta
                        </button>
                        <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                            <li><a class="dropdown-item"   onClick= {this.seleccionC} id="Fondant ">Fondant </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionC} id="Crema Pastelera">Crema Pastelera </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionC} id="Crema de Chantilly"> Crema de Chantilly </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionC} id="Crema de Mantequilla"> Crema de Mantequilla </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionC} id="Sin Cobertura"> Sin Cobertura </a></li>
                        </ul>
                    </div>
                    
                </div> 
                <div style ={{margin:5+'px'}}>
                    <div class="dropdown">
                        <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-toggle="dropdown" aria-expanded="false">
                             Porciones
                        </button>
                        <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                            <li><a class="dropdown-item"   onClick= {this.seleccionP} id="1-35 ">1-35 </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionP} id="35-60">35-60 </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionP} id="60-100"> 60-100</a></li>
                        </ul>
                    </div>
                    
                </div> 
                <div style ={{margin:5+'px'}}>
                    <div class="dropdown">
                        <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-toggle="dropdown" aria-expanded="false">
                             Tematica
                        </button>
                        <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                            <li><a class="dropdown-item" onClick= {this.seleccionT} id="Matrimonio ">Matrimonio </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionT} id="Grado ">Grado </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionT} id="Cumpleaños"> Cumpleaños </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionT} id="Comunion"> Comunion </a></li>
                            <li><a class="dropdown-item" onClick= {this.seleccionT} id="Casual"> Casual </a></li>
                        </ul>
                    </div>
                    {console.log(this.state )}
                </div> 
                                             
            </div>
            </div>
               
                        
        )
    }

}

export  class Pastel extends React.Component{
    componentDidMount() {
        var scene = new THREE.Scene();
        var camera = new THREE.PerspectiveCamera(
            75,
            window.innerWidth / window.innerHeight,
            0.1,
            1000
        );
        
        var renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.shadowMap.enabled = true;
        renderer.shadowMap.type = THREE.PCFSoftShadowMap; // default THREE.PCFShadowMap
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.body.appendChild(renderer.domElement);
        
        var controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableZoom = true;
        
        var candleFire = new THREE.PointLight(0xffffff, 0.8, 25);
        candleFire.castShadow = true;
        candleFire.position.y = 5.6;
        scene.add(candleFire);
        
        var visibleFlameGeom = new THREE.CylinderGeometry(0.01, 0.15, 0.5, 4, 4);
        var flameModifier = new THREE.BufferSubdivisionModifier(4);
        var flameSmooth = flameModifier.modify(visibleFlameGeom);
        var visibleFlameMat = new THREE.MeshPhongMaterial({
            color: 0xffffff,
            emissive: 0xff7326,
            specular: 0xffffff,
            shininess: 0.5
        });
        var visibleFlame = new THREE.Mesh(flameSmooth, visibleFlameMat);
        visibleFlame.position.y = candleFire.position.y - 1.1;
        scene.add(visibleFlame);
        
        var universeGeom = new THREE.SphereGeometry(60, 32, 32);
        var universeMat = new THREE.MeshPhongMaterial({
            color: 0x000000,
            side: THREE.BackSide
        });
        var universe = new THREE.Mesh(universeGeom, universeMat);
        scene.add(universe);
        
        var points = [];
        for (var i = 0; i < 10; i++) {
            points.push(new THREE.Vector2(Math.sin(i * 0.2) * 10 + 5, (i - 5) * 2));
        }
        
        var cakeGeom = new THREE.CylinderGeometry(5, 5, 1, 6, 6);
        var modifier = new THREE.BufferSubdivisionModifier(4);
        var smooth = modifier.modify(cakeGeom);
        var cakePink = new THREE.MeshPhongMaterial({
            color: 0xffe9fc,
            emissive: 0xc9669c,
            emissiveIntensity: 0.3,
            shininess: 0
        });
        var cakeBeige = new THREE.MeshPhongMaterial({
            color: 0xf5d598,
            emissive: 0xfff2b0,
            emissiveIntensity: 0.3,
            shininess: 0
        });
        var cake1 = new THREE.Mesh(smooth, cakePink);
        var cake2 = new THREE.Mesh(smooth, cakeBeige);
        var cake3 = new THREE.Mesh(smooth, cakePink);
        var cake4 = new THREE.Mesh(smooth, cakeBeige);
        cake1.receiveShadow = true;
        cake1.castShadow = true;
        cake1.position.y = 0;
        cake2.receiveShadow = true;
        cake2.castShadow = true;
        cake2.position.y = 0.8;
        cake3.receiveShadow = true;
        cake3.castShadow = true;
        cake3.position.y = 1.6;
        cake4.receiveShadow = true;
        cake4.castShadow = true;
        cake4.position.y = 2.4;
        
        scene.add(cake1);
        scene.add(cake2);
        scene.add(cake3);
        scene.add(cake4);
        
        var bougieGeom = new THREE.CylinderGeometry(0.1, 0.1, 2, 6, 6);
        var bougieMat = new THREE.MeshStandardMaterial({ color: 0xffffff, emissive: 0xf2f2f2, emissiveIntensity: 0.3 });
        var bougie = new THREE.Mesh(bougieGeom, bougieMat);
        bougie.position.y = 3.2;
        scene.add(bougie);
        
        var planeGeom = new THREE.PlaneGeometry(1000, 1000, 4, 4);
        var planeMat = new THREE.MeshPhongMaterial({ color: 0xffd9c4, shininess: 0 });
        var plane = new THREE.Mesh(planeGeom, planeMat);
        plane.rotation.x = -Math.PI / 2;
        plane.position.y = -0.5;
        plane.receiveShadow = true;
        scene.add(plane);
        
        camera.position.z = 20;
        camera.position.y = 10;
        camera.lookAt(cake2.position.x, cake2.position.y, cake2.position.z);
        
        var frameX = 0.5;
        var frameY = 0.5;
        var frameZ = 0.5;
        
        var animate = function() {
            //light animation
        
            requestAnimationFrame(animate);
            candleFire.intensity += Math.sin(frameX) / 30;
            candleFire.position.x += Math.sin(frameX) / 30;
            candleFire.position.z += Math.sin(frameZ) / 10;
            candleFire.position.y += Math.sin(frameY) / 20;
        
            visibleFlame.scale.y += Math.sin(frameY) / 20;
            visibleFlame.scale.x += Math.sin(frameX) / 30;
            visibleFlame.scale.z += Math.sin(frameZ) / 10;
        
            frameY += 0.3;
            frameX += 0.4;
            frameZ += 0.2;
            //cube.rotation.x += 0.1;
            //cube.rotation.y += 0.1;
        
            renderer.render(scene, camera);
        };
        
        animate();
        
        //Rend la scene responsive
        window.addEventListener("resize", onWindowResize, false);
        
        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
        
            renderer.setSize(window.innerWidth, window.innerHeight);
        }
        
        
      }
      render() {
        return (
          <div />
        )
      }
}
