import React, {useState,useRef} from "react";

class Coberturas extends React.Component{
    render(){
        return(
            <form>
                <h1>{"Coberturas"}</h1>
                
<input type="radio" name="cob" value="1"/>Fondant
<select>
  <option value="Azul">Azul</option>
  <option value="Verde" selected>Verde</option>
  <option value="Rojo">Rojo</option>
</select><br />
<input type="radio" name="cob" value="2"/>Crema Pastelera<br />
<input type="radio" name="cob" value="3"/>Crema de Chantilly <br />
<input type="radio" name="cob" value="4"/>Crema de Mantequilla<br />
<input type="radio" name="cob" value="5"/>Sin Cobertura<br />


            </form>
        )
    }
}


export default Coberturas;
