import "./style.scss";
import React, { useState, useRef } from "react";

class Coberturas extends React.Component {
    
    constructor(props){
        super(props);
        this.cobertura=React.createRef();
    }

    clicRefs(evento){
        console.log(this.cobertura.current.value);

    }
    render() {
        return (
            <div className="container">
                <div className="cake">

                </div>
                <div className="option">
                    <form>
                        <h1>{"Coberturas"}</h1>

                        <select>
                            <option value="Fondant" onClick={this.clicRefs.bind(this)}>Fondant</option>
                            <option value="Azul">Azul</option>
                            <option value="Verde" >Verde</option>
                            <option value="Rojo">Rojo</option>
                        </select><br />
                        <input type="radio" name="cob" value="2" option value="Azul" onClick={this.clicRefs.bind(this)}/>Crema Pastelera<br />
                        <input type="radio" name="cob" value="3" onClick={this.clicRefs.bind(this)}/>Crema de Chantilly <br />
                        <input type="radio" name="cob" value="4" onClick={this.clicRefs.bind(this)}/>Crema de Mantequilla<br />
                        <input type="radio" name="cob" value="5" onClick={this.clicRefs.bind(this)}/>Sin Cobertura<br />

                        <button>
                    Siguiente
                </button>
                    </form>
                </div>
                
            </div>
        )
    }
}


export default Coberturas;
