import React,{useState,useRef} from 'react';
import ReactDOM from 'react-dom';


class InputSelect extends React.Component{
  constructor(props){
    super(props)
    this.actualizarState = this.actualizarState.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state={activo:""};
  }
  actualizarState(e){
    const {name, value} = e.target;
    this.setState({value});
    this.props.actualizarState({
      name,value,error: value == "" ? true:false
    })
  }
  handleSubmit(e) {
    alert('A name was submitted: ' + this.state.value);
    e.preventDefault();
  }
  
  render(){
    return(
      <div>


      
      <div className="componente-input"  >
        <label htmlFor={"id-"+this.props.name}>{this.props.label}</label>
        <select
          id={"id-"+this.props.name}
          name={this.props.name}
          onChange={this.actualizarState}
          >
            {
              this.props.opciones.map((opcion,index)=>(
              <option hey={index} value={opcion.value}>{opcion.texto}</option>
              ))
            }            
        </select>
        
        
      </div>
      
      </div>
    )
  }
}

class Coberturas extends React.Component{

  constructor(props){
    super(props);
    this.submit = this.submit.bind(this);
    this.actualizarState = this.actualizarState.bind(this);
    this.state = {
      
      opciones:{
        value:"",
        error:true
      }
    }
  }
  
  actualizarState(input){
    this.setState({
      ...this.state,
      [input.name]:{
        value:input.value,
        error:input.error
      }
    }, ()=>{console.log(this.state);});
    
  }

  submit(e){
    e.preventDefault();
    console.log(this.state)
    }

  render(){
    const valor =this.state.opciones.value;
    const color='green';
    {if (valor === '3') { 
      //color='black';
      console.log('Got John');
} else { 
  //color='white';
  console.log('Got someone else'); } }
    
    return(
      
      <div>
      <form onSubmit={this.submit} >
        <h1>{"Coberturas"}</h1>
      
        <InputSelect
          name="opciones"
          label="Elige una opción:"
          actualizarState={this.actualizarState}
          opciones={[
            {value:"", texto:"Seleccione un opción..."},        
            {value:"1", texto:"Fondant"},
            {value:"2", texto:"Crema Pastelera"},
            {value:"3", texto:"Crema de Chantilly"},
            {value:"4", texto:"Crema de Mantequilla"},
            {value:"5", texto:"Sin Cobertura"}
            
            ]} />


        <button
          disabled={ this.state.opciones.error}
          type="submit"
          className={
            this.state.opciones.error
            ? 
            "button-disable":"button"}>
          Siguiente
        </button>
       
      </form>
      
      <div className={valor} >
        
    {valor}
        
      
      </div>
      </div>
    )
  }
}

ReactDOM.render(
  <Coberturas/>,
  document.getElementById('root')
);


export default Coberturas;
