import React, {useState} from 'react';
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./style.scss";
import {SliderPicker} from 'react-color';


export default class Pedido extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
                Masa: 'Sin masa',
                Relleno: 'Sin relleno',
