import React from "react";
import Cookies from 'js-cookie';
import Modal from "react-responsive-modal";


export class Login extends React.Component {

    constructor(props) {
        super(props);
        this.input = React.createRef();
        this.state = {
            test : null,
            sign: false,
            login: false,
        }
    }

    onOpenModal = () => {
        this.setState({ sign: true });
    };

    onOpenModalLogin = () => {
        this.setState({ login: true });
    };

    onCloseModal = () => {
        this.setState({ sign: false });
    };

    onCloseModalclose = () => {
        this.setState({ login: false });
    };

    componentDidMount() {
        this.renderLogin()
        window.addEventListener('submit', this.login)
    }


    renderLogin = async () => {
        try {
            let res = await fetch("http://localhost:8000/accounts/login/")
            let html = await res.text();

            let parser = new DOMParser();
            let doc = parser.parseFromString(html, "text/html");
            Cookies.set('csrftoken', doc.getElementsByTagName('input').namedItem('csrfmiddlewaretoken').value)
            doc.getElementsByTagName('input').namedItem('login').setAttribute('name', 'username')
            doc.getElementsByTagName('input').namedItem('username').setAttribute('ref', this.input)
            // doc.getElementsByTagName('form')[0].addEventListener('submit', this.handleSubmit)

            this.setState({
                test : doc.body.innerHTML,
            });
        } catch (err) {
            console.log(err);
        }
    }

    login = async (event) => {
        event.preventDefault();
        let user = document.getElementsByTagName('input').namedItem('username').value
        let pass = document.getElementsByTagName('input').namedItem('password').value

            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json'},
                credentials: "include",
                body : JSON.stringify({"email" : user, "password" : pass})
        };

        let response = await fetch('http://localhost:8000/users/api/auth/login/', requestOptions)
        if (response.statusText === 'OK'){
            let usr = await fetch('http://localhost:8000/users/api/auth/user/', {credentials: "include"})
            let js = await usr.json()

            console.log("usuario: " + JSON.stringify(js))
        } else{
            let js = await response.json()
            if(response.status === 400){
                console.log("credenciales incorrectas")
            } else if(response.status === 403){
                console.log("sesion ya iniciada " + JSON.stringify(js))
            } else {
                console.log("otro error: "  + JSON.stringify(js) + JSON.stringify(response))
            }
        }    
    }

    render() {
        const { login, sign } = this.state;
        return (

            <>
                <header className="header header-animated opaque" style={{ "display": 'block', "paddingTop": "5px", "paddingBottom": "5px" }}>
                    <div className="container">
                        <nav className="navbar navbar-default" role="navigation">
                            <div className="navbar-header">
                                
                            </div>


                            <div className="nav-toggle collapsed" data-toggle="collapse" data-target="#navbarMain" aria-expanded="false" style={{ "top": "15px" }}>
                                <span></span>
                                <span></span>
                                <span></span>
                                <span></span>
                                <span></span>
                                <span></span>
                            </div>
                            {/* <!-- Collect the nav links, forms, and other content for toggling --> */}
                            <div className="navbar-collapse collapse in" id="navbarMain" aria-expanded="true" style={{ top: "65px" }}>

                                <ul className="nav navbar-nav navbar-right">
                                    <li>
                                        <button className="btn btn-primary-outline" id="signup" onClick={this.onOpenModal}>SignUp</button>
                                    </li>
                                    <li>
                                        <button className="btn btn-primary-outline" id="login" onClick={this.onOpenModalLogin}>Login</button>
                                    </li>
                                </ul>
                                <ul className="nav navbar-nav collapsed-color">

                                    <li>
                                        <a href="#">Home</a>
                                    </li>

                                    <li>
                                        <a href="#">About</a>
                                    </li>

                                    <li>
                                        <a href="#">Features</a>
                                    </li>

                                    <li>
                                        <a href="#">Blog</a>
                                    </li>
                                    <li>
                                        <a href="#">Products</a>
                                    </li>

                                </ul>

                            </div>

                            {/* <!-- .navbar-collapse --> */}
                        </nav>
                    </div>

                </header>
                {/* Sign up model */}

                <Modal open={sign} onClose={this.onCloseModal}>
                    <div className="modal-body">
                        <h2>Get Started Absolutely<span> Free!</span></h2>
                        <span className="subtitle">No credit card needed</span>
                        <form className="contact-form form-validate3" novalidate="novalidate">
                            <div className="form-group">
                                <input className="form-control" type="text" name="name" id="name" placeholder="First Name" required="" autocomplete="off" aria-required="true" />
                            </div>
                            <div className="form-group">
                                <input className="form-control" type="email" name="email" placeholder="E-mail" required="" autocomplete="off" aria-required="true" />
                            </div>
                            <div className="form-group">
                                <input type="password" name="pass" className="form-control" placeholder="Password" required="" autocomplete="off" aria-required="true" />
                            </div>
                            <input className="btn btn-md btn-primary btn-center" id="sign_up" type="button" value="Sign Up" />
                        </form>
                    </div>
                </Modal>

                {/* <!-- signUp End -->
                  <!-- login --> */}

                <Modal open={login} onClose={this.onCloseModalclose}>
                
                    <div className="modal-body">
                        <h2>Login and Get <span>Started</span></h2>
                        <span className="subtitle">Just fill in the form below</span>
                        <form className="contact-form form-validate4" novalidate="novalidate">
                            <div className="form-group">
                                <input className="form-control" type="email" name="email" placeholder="E-mail" required="" autocomplete="off" aria-required="true" />
                            </div>
                            <div className="form-group">
                                <input type="password" name="pass" className="form-control" placeholder="Password" required="" autocomplete="off" aria-required="true" />
                            </div>
                            <input className="btn btn-md btn-primary btn-center" id="login_btn" type="button" value="Login" />
                        </form>
                    </div>
                </Modal>
            </>
            );
    }

}
